import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_PUBLISHABLE_KEY = import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY;

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
  auth: {
    storage: localStorage,
    persistSession: true,
    autoRefreshToken: true,
    detectSessionInUrl: true,
  }
});

// Clear invalid sessions on startup with JWT validation
supabase.auth.onAuthStateChange(async (event, session) => {
  console.log('üîê Auth state changed:', event, session ? 'Session exists' : 'No session');
  
  // ‚úÖ Validate session has valid access_token with sub claim
  if (session && session.access_token) {
    try {
      const payload = JSON.parse(atob(session.access_token.split('.')[1]));
      if (!payload.sub) {
        console.error('‚ùå Invalid token: missing sub claim');
        await supabase.auth.signOut();
        return;
      }
    } catch (error) {
      console.error('‚ùå Token decode error:', error);
      await supabase.auth.signOut();
      return;
    }
  }
  
  if (event === 'TOKEN_REFRESHED' && !session) {
    console.log('‚ö†Ô∏è Session refresh failed, clearing storage');
    localStorage.removeItem('supabase.auth.token');
    sessionStorage.clear();
  }
  
  // Session management is handled automatically by Supabase client
  // with autoRefreshToken: true. Manual validation removed to prevent deadlocks.
});